* {
    padding: 0;
    margin: 0;
}

body {
}

a, body {
    font-family: "Segoe UI Light","Segoe UI",Segoe,Tahoma,Arial,Verdana,sans-serif;
}

  container {
        margin-bottom: 50px;
        margin: auto;
    }


.card {
        /*padding-left: 180px;
        width: 920px;*/
        margin-top: 10px;
        line-height: 20px;
        float: left;
		font-family: Arial,Verdana,sans-serif;
    }

        .card p {
            padding: 10px 0;
			position: relative;
        }

            .card p.word {
                font-size: 30px;
                border-bottom: 1px dashed #D2D0C8;
                color: #00AEEF;
            }
                .card p.word i {
                    color: #AAAAAA;
                    font-size: 16px;
                    font-style: normal;
                }

                    .card p.word i:before {
                        content: "-";
                        padding-right: 10px;
                        color: #666666;
                    }

            .card p u {
                font-size: 18px;
                text-decoration: none;
                color: #FF8A00;
            }

			.card p tt{
                font-family: Arial,Verdana,sans-serif;
    				font-size: 22px;
    				margin-left: 30px;
            }
            .card p .ex {
                border-left: 1px solid #CCCCCC;
                display: block;
                padding-top: 5px;
				margin-top:2px;
                padding-left: 20px;
            }

                .card p .excontent {
                    border-left: 1px solid #CCCCCC;
                    /*display: block;*/
                    padding-bottom: 5px;
                    padding-left: 40px;
                }

                    .card p .excontent:before {
                        content: "-";
                        padding-right: 5px;
                    }

#bottom-left {
  text-align:left;
  position:fixed;
  bottom: 20px;
  left: 20px;
  width: 33vw;
}
